textAlign: "center",
},
valueContainer: {
  flexDirection: "row",
  alignItems: "center",
  marginBottom: 20,
  borderWidth: 2,
  borderRadius: 25,
  padding:12,
  borderColor:'mediumturquoise',
  flexWrap: 'wrap'
},
label: {
  alignItems:'center',
